import"https://cdn.jsdelivr.net/npm/chart.js@4.2.0/dist/chart.umd.min.js";function load(){let start=new Date;function getRandomData(){return Array.from({length:7},(()=>Math.ceil(30*Math.random())))}function currentUptime(){document.getElementById("uptime").innerHTML=new Date(Date.now()-start)}function config(t,e,a){return{labels:["Poniedziałek","Wtorek","Środa","Czwartek","Piątek","Sobota","Niedziela"],datasets:[{label:t,data:e,backgroundColor:a,borderWidth:1}],options:{scales:{y:{beginAtZero:!0}}}}}const uptime=window.setInterval(currentUptime,1e3),ctx1=document.getElementById("chart1"),ctx2=document.getElementById("chart2"),ctx3=document.getElementById("chart3"),ctx4=document.getElementById("chart4"),chart1=new Chart(ctx1,{type:"bar",data:config("Temperatura (°C)",getRandomData(),"#FF457A")}),chart2=new Chart(ctx2,{type:"bar",data:config("Prędkość wiatru (km/h)",getRandomData(),"#DB2559")}),chart3=new Chart(ctx3,{type:"bar",data:config("Ciśnienie (hPa)",getRandomData(),"#0F8F1E")}),chart4=new Chart(ctx4,{type:"bar",data:config("Opady (mm)",getRandomData(),"#25DB3C")}),bcb=document.getElementById("bar_chart"),lcb=document.getElementById("line_chart");function viewBar(){for(let i=1;i<5;i++){let current_chart=eval("chart"+i);current_chart.config.type="bar",current_chart.update()}bcb.classList.add("btn-primary"),bcb.classList.remove("btn-secondary"),lcb.classList.add("btn-secondary")}function viewLine(){for(let i=1;i<5;i++){let current_chart=eval("chart"+i);current_chart.config.type="line",current_chart.update()}lcb.classList.add("btn-primary"),lcb.classList.remove("btn-secondary"),bcb.classList.add("btn-secondary")}bcb.addEventListener("click",viewBar,!1),lcb.addEventListener("click",viewLine,!1)}window.onload=t=>{load()};